import { Box, Typography, styled } from "@mui/material";

const HeadingWrapper = styled(Box)`
padding: 15px 24px;
background: #fff;
border-bottom: 1px solid #f0f0f0;
`;
const Heading = styled(Typography)`
color: #878787;
`;
const Container = styled(Box)`
padding: 15px 24px;
background: #fff;
& > p {
    margin-bottom: 20px;
    font-size: 14px;
}
`;

const Price = styled(Box)`
float: right;
`;

const TotalPaymentView = ({ cartItems }) => {
    return (
        <Box>
            <HeadingWrapper>
                <Heading>PRICE DETAILS</Heading>
            </HeadingWrapper>
            <Container>
                <Typography>Price({cartItems?.length} item)
                    <Price component="span">₹100</Price>
                </Typography>
                <Typography>Discount
                    <Price component="span">₹100</Price>
                </Typography>
                <Typography>Delivery Charges
                    <Price component="span">₹100</Price>
                </Typography>
                <Typography component="h6">Total Amount
                    <Price component="span">₹100</Price>
                </Typography>
                <Typography>You will save ₹100 on this order</Typography>
            </Container>
        </Box>
    )
}
export default TotalPaymentView;
