import { Typography, Box, Table, TableBody, TableRow, TableCell, styled } from "@mui/material";
import { LocalOffer as Badge } from '@mui/icons-material';

const SmallText = styled(Box)`
font-size: 14px;
vertical-align: baseline;
& > p {
  font-size: 14px;
  margin-top: 10px;
}
`;
const StyleBadge = styled(Badge)`
margin-right: 10px;
color: #00CC00;
font-size: 15px;
`;

const ProductDetail = ({ product}) => {
    const fassured = 'https://static-assets-web.flixcart.com/www/linchpin/fk-cp-zion/img/fa_62673a.png'
    const adURL = 'https://rukminim1.flixcart.com/lockin/774/185/images/CCO__PP_2019-07-14.png?q=50';

    const date = new Date(new Date().getTime()+(5 * 24 * 60 * 60 * 1000));

  return (
    <>
      <Typography>{product.title.longTitle}</Typography>
      <Typography style={{ marginTop: 5, color: "#878787", fontSize: 14 }}>
        8 Ratings & 1 Reviews
        <Box component="span">
          <img
            src={fassured}
            alt="assuredlogo"
            style={{ width: 77, marginTop: 20 }}
          />
        </Box>
      </Typography>
      <Typography>
        <Box component="span" style={{ fontSize: 28 }}>
          ₹{product.price.cost}
        </Box>
        &nbsp;&nbsp;&nbsp;
        <Box component="span" style={{ color: "#878787" }}>
          <strike>₹{product.price.mrp}</strike>
        </Box>
        &nbsp;&nbsp;&nbsp;
        <Box component="span" style={{ color: "#388E3C" }}>
          ₹{product.price.discount}
        </Box>
      </Typography>
      <Typography>Available Offers</Typography>
      <SmallText>
        <Typography><StyleBadge />Get extra 20% off upto ₹50 on 1 item(s) T&C</Typography>
        <Typography><StyleBadge />Get extra 23% off (price inclusive of discount) T&C</Typography>
        <Typography><StyleBadge />Sign up for Flipkart Pay Later and get Flipkart Gift Card worth ₹100* Know More</Typography>
        <Typography><StyleBadge />Buy 2 items save 5%;Buy 3 or more save 10% T&C</Typography>
        <Typography><StyleBadge />5% Cashback on Flipkart Axis Bank Card</Typography>
        <Typography><StyleBadge />No Cost EMI on Bajaj Finserv EMI Card on cart valu above ₹2999 T&C</Typography>
      </SmallText>
      <Table>
        <TableBody>
          
          <TableRow>
            <TableCell style={{ color: '#878787' }}>Delivery</TableCell>
            <TableCell style={{ fontWeight: 600 }}>Delivery by {date.toDateString()} | ₹40</TableCell>
          </TableRow>

          <TableRow>
            <TableCell style={{ color: '#878787' }}>Warranty</TableCell>
            <TableCell >No Warranty</TableCell>
          </TableRow>

          <TableRow>
            <TableCell style={{ color: '#878787' }}>Seller</TableCell>
            <TableCell>
              <Box component="span" style={{ color: '#2874f0 ' }}>SuperComNet</Box>
              <Typography>GST invoice available</Typography>
              <Typography>View more sellers starting from ₹{product.price.cost}</Typography>
            </TableCell>
          </TableRow>

          <TableRow>
            <TableCell colSpan={2}>
              <img src={adURL} style={{ width: 390 }} alt="flipkartpoint" />
            </TableCell>
          </TableRow>

          <TableRow>
            <TableCell style={{ color: '#878787' }}>Description</TableCell>
            <TableCell >{product.description}</TableCell>
          </TableRow>

        </TableBody>
      </Table>
    </>
  );
};
export default ProductDetail;
