import Slide from './Slide'

import { Box, styled } from "@mui/material";

const Component = styled(Box)`
display: flex;
`;
const LeftComponent = styled(Box)`
width: 83%;
`;
const RightAdsComponent = styled(Box)`
background: #ffffff;
padding: 5px;
margin-top: 10px;
margin-left: 10px;
width: 17%
text-align: center;
`;

const AdsSlide = ({ products, title,timer }) => {
    const adURL = 'https://rukminim1.flixcart.com/flap/464/708/image/633789f7def60050.jpg?q=70';
    return (
        <Component>
            <LeftComponent>
                <Slide 
                  products={products}
                  title={title}
                  timer={timer}
                />
            </LeftComponent>
            <>
                <img src={adURL} alt="ad" style={{ width: 217 }} />
            </>
        </Component>
    )
}
export default AdsSlide;
