min-width: 40%;
padding: 40px 0 0 80px;
`;
const Image = styled('img')({
    padding: '15px 20px',
    border: '1px solid #f0f0f0',
    width:'95%'
});
const StyledButton = styled(Button)`
width: 40%;
height: 50px;
border-radius: 2px;
`;

const ActionItem = ({ product }) => {
    return (
        <LeftContainer>
            <Image src={product.detailUrl} alt="productImage" />
            <StyledButton variant="contained" style={{ marginRight: 10, background: '#ff9f00'}}> Add to Cart</StyledButton>
            <StyledButton variant="contained" style={{ backgroun}}>Buy Now</StyledButton>
        </LeftContainer>
    )
}
export default ActionItem;
