import { Menu } from "@mui/icons-material";
import { List, ListItem, Box, IconButton, Drawer, styled } from "@mui/material";
import { useState } from "react";

const MenuButton = styled(IconButton)(({ theme }) => ({
  display: "none",
  [theme.breakpoints.down("md")]: {
    display: "block",
  },
}));
const Wrapper = styled(Box)(({ theme }) => ({
    display: "flex",
    margin: "0 6% 0 2.5%",
    // [theme.breakpoints.down("md")]: {
    //   display: 'none',
    // },
    [theme.breakpoints.down("md")]: {
      display: 'none',
    }
  }));
   
  const BecomeAndMoreText = styled(Typography)`
    margin-right: 40px;
    font-size: 16px;
    align-items: center;
  `;
  
  const CartContainer = styled(Box)(({ theme }) => ({
    display: 'flex',
    [theme.breakpoints.down('md')]: {
      display: 'block'
    }
  }));
  
  const LoginButton = styled(Button)`
    color: #2874f0;
    background: #fff;
    text-transform: none;
    padding: 5px 40px;
    border-radius: 2px;
    box-shadow: none;
    font-weight: 600;
    height: 32px;
  `;

const MenuIcon = () => {

  const [open, setOpen] = useState(false);
  const handleOpen = () => {
    setOpen(true);
  }
  const handleClose = () => {
    setOpen(false);
  }

  const list = () => {
    <Box style={{ width: 200 }} onClick={handleClose}>
        <List>
            <ListItem button>
            <Wrapper>
            <LoginButton
              variant="contained"
              onClick={() => openDialog()}
              style={{ marginLeft: 15 }}
            >
              Login
            </LoginButton>
          

          <BecomeAndMoreText
            style={{ marginLeft: 40, marginTop: 3, width: 135 }}
          >
            Become a Seller
          </BecomeAndMoreText>
          <BecomeAndMoreText style={{ marginTop: 3 }}>More</BecomeAndMoreText>

          <CartContainer>
            <ShoppingCartIcon />
            <Typography>Cart</Typography>
          </CartContainer>
          
        </Wrapper>
            </ListItem>
        </List>
    </Box>
  }

  return (
    <>
      <MenuButton color="inherit" onClick={handleOpen}>
        <Menu />
      </MenuButton>
      <Drawer open={open} onClose={handleClose}>
        {list()}
      </Drawer>
    </>
  );
};
export default MenuIcon;
