import { Typography, Box } from "@mui/material";
import { LocalOffer as Badge } from '@mui/icons-material';



const ProductDetail = ({ product}) => {
    const fassured = 'https://static-assets-web.flixcart.com/www/linchpin/fk-cp-zion/img/fa_62673a.png'
  return (
    <>
      <Typography>{product.title.longTitle}</Typography>
      <Typography style={{ marginTop: 5, color: "#878787", fontSize: 14 }}>
        8 Ratings & 1 Reviews
        <Box component="span">
          <img
            src={fassured}
            alt="assuredlogo"
            style={{ width: 77, marginTop: 20 }}
          />
        </Box>
      </Typography>
      <Typography>
        <Box component="span" style={{ fontSize: 28 }}>
          ₹{product.price.cost}
        </Box>
        &nbsp;&nbsp;&nbsp;
        <Box component="span" style={{ color: "#878787" }}>
          <strike>₹{product.price.mrp}</strike>
        </Box>
        &nbsp;&nbsp;&nbsp;
        <Box component="span" style={{ color: "#388E3C" }}>
          ₹{product.price.discount}
        </Box>
      </Typography>
      <Typography>Available Offers</Typography>
      <Box>
        <Typography><Badge />Get extra 20% off upto ₹50 on 1 item(s) T&C</Typography>
        <Typography><Badge />Get extra 23% off (price inclusive of discount) T&C</Typography>
        <Typography><Badge />Sign up for Flipkart Pay Later and get Flipkart Gift Card worth ₹100* Know More</Typography>
        <Typography><Badge />Buy 2 items save 5%;Buy 3 or more save 10% T&C</Typography>
        <Typography>5% Cashback on Flipkart Axis Bank Card</Typography>
        <Typography><Badge />No Cost EMI on Bajaj Finserv EMI Card on cart valu above ₹2999 T&C</Typography>
      </Box>
    </>
  );
};
export default ProductDetail;
