import { Menu } from "@mui/icons-material";
import { List, ListItem, Box, IconButton, Drawer, styled } from "@mui/material";
import { useState } from "react";
import { Wrapper, LoginButton } from '../header/Header';
const MenuButton = styled(IconButton)(({ theme }) => ({
  display: "none",
  [theme.breakpoints.down("md")]: {
    display: "block",
  },
}));

const MenuIcon = () => {

  const [open, setOpen] = useState(false);
  const handleOpen = () => {
    setOpen(true);
  }
  const handleClose = () => {
    setOpen(false);
  }

  const list = () => {
    <Box style={{ width: 200 }} onClick={handleClose}>
        <List>
            <ListItem button>
                <Wrapper>
                    <LoginButton />
                </Wrapper>
            </ListItem>
        </List>
    </Box>
  }

  return (
    <>
      <MenuButton color="inherit" onClick={handleOpen}>
        <Menu />
      </MenuButton>
      <Drawer open={open} onClose={handleClose}>
        {list()}
      </Drawer>
    </>
  );
};
export default MenuIcon;
