import Slide from './Slide'

import { Box, styled } from "@mui/material";

const Component = styled(Box)`
display: flex;
`;
const LeftComponent = styled(Box)`
width: 83%;
`;
const RightAdsComponent = styled(Box)(({ theme }) => ({
background: '#ffffff',
padding: 5,
marginTop: 10,
marginLeft: 10,
width: '17%',
textAlign: 'center',
[theme.breakpoints.down('md')]: {
    display: none
}
}));

const AdsSlide = ({ products, title,timer }) => {
    const adURL = 'https://rukminim1.flixcart.com/flap/464/708/image/633789f7def60050.jpg?q=70';
    return (
        <Component>
            <LeftComponent>
                <Slide 
                  products={products}
                  title={title}
                  timer={timer}
                />
            </LeftComponent>
            <RightAdsComponent>
                <img src={adURL} alt="ad" style={{ width: 217 }} />
            </RightAdsComponent>
        </Component>
    )
}
export default AdsSlide;
